mapper.select:
select

from j_bank_card
where 1=1

		and user_id = #{userId,jdbcType=INTEGER}


		and server_id = #{serverId,jdbcType=INTEGER}


		and img_url = #{imgUrl,jdbcType=VARCHAR}


		and bank_address = #{bankAddress,jdbcType=VARCHAR}


		and bank_num = #{bankNum,jdbcType=VARCHAR}


		and username = #{username,jdbcType=VARCHAR}


		and bank_id = #{bankId,jdbcType=INTEGER}


		and type = #{type,jdbcType=VARCHAR}


		and create_dtm = #{createDtm,jdbcType=TIMESTAMP}

	limit 0,1

select

from j_bank_card
where 1=1

		and user_id = #{userId,jdbcType=INTEGER}


		and server_id = #{serverId,jdbcType=INTEGER}


		and img_url = #{imgUrl,jdbcType=VARCHAR}


		and bank_address = #{bankAddress,jdbcType=VARCHAR}


		and bank_num = #{bankNum,jdbcType=VARCHAR}


		and username = #{username,jdbcType=VARCHAR}


		and bank_id = #{bankId,jdbcType=INTEGER}


		and type = #{type,jdbcType=VARCHAR}


		and state = #{state,jdbcType=INTEGER}


		and create_dtm = #{createDtm,jdbcType=TIMESTAMP}

	order by ${sort} ${sortUp}
	limit #{start},#{limit}

